import streamlit as st
from transformers import pipeline

import streamlit as st

def get_response(user_input):
    user_input = user_input.lower()

    if "menu" in user_input:
        resposta = "Nosso menu inclui hambúrgueres, batatas fritas, milkshakes, sobremesas e muito mais. O que você gostaria de pedir?"
    elif "horário" in user_input or "funcionamento" in user_input:
        resposta = "Estamos abertos todos os dias, das 10h às 23h."
    elif "pedido" in user_input:
        resposta = "Para fazer um pedido, basta escolher o que deseja no nosso menu e falar 'Quero isso'."
    elif "oferta" in user_input:
        resposta = "Atualmente, temos uma promoção de combo com batatas grandes e refrigerante. Aproveite!"
    elif "carne humana" in user_input:
        resposta = "Atualmente o McDonald's vende apenas carne de borracha, mas existe um código secreto para que você consiga um negosinho."
        imagem = "data:image/jpeg;base64,"  
    else:
        resposta = "Desculpe, não entendi sua pergunta. Pode reformular?"
        imagem = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSKDK8X6fsBkWJrEEKdiD3jTkAE1spjMIh3Qw&s" 

    return resposta, imagem

st.title("Chatbot McDonald's")
st.write("Olá! Sou o assistente virtual do McDonald's. Como posso ajudar você hoje?")

user_input = st.text_input("Digite sua pergunta:")

if user_input:
    response, image_path = get_response(user_input)
    
    st.write("Chatbot: " + response)
    
    st.image(image_path, caption="Imagem relacionada", use_container_width=True)

